import '../assets/Style/App.css';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
// import { Parallax, ParallaxLayer } from "@react-spring/parallax";

import TextBlock  from '../Components/TextBlock.js';

function Mainblock(){

    return (
      // ? pages indicate that how many pages you want to display . we can add multiple layer in a single page

      // ? offset indicates the page number .. like offset 0, 1, 2, 3,  means  page 1, page 2, page 3, page 4

      <Parallax pages={2} style={{ top: "0", left: "0" }} className="animation">
        <ParallaxLayer offset={0} speed={2.5}>
          <div className="animation_layer parallax" id="backgrounds"></div>
        </ParallaxLayer>
        <ParallaxLayer offset={0} speed={2.5}>
          <div className="animation_layer parallax" id="mountain"></div>
        </ParallaxLayer>{" "}
        <ParallaxLayer offset={0} speed={2.5}>
          <div className="animation_layer parallax" id="logoLand"></div>
        </ParallaxLayer>{" "}
        <ParallaxLayer offset={0} speed={2.5}>
          <div className="animation_layer parallax" id="j1"></div>
        </ParallaxLayer>
        <ParallaxLayer offset={0} speed={2.5}>
          <div className="animation_layer parallax" id="j2"></div>
        </ParallaxLayer>
        <ParallaxLayer offset={0} speed={2.5}>
          <div className="animation_layer parallax" id="j3"></div>
        </ParallaxLayer>
        <ParallaxLayer offset={0} speed={2.5}>
          <div className="animation_layer parallax" id="j4"></div>
        </ParallaxLayer>{" "}
        <ParallaxLayer offset={0} speed={2.5}>
          <div className="animation_layer parallax" id="manOnMountain"></div>
        </ParallaxLayer>{" "}
        <ParallaxLayer offset={0} speed={2.5}>
          <div className="animation_layer parallax" id="j5"></div>
        </ParallaxLayer>{" "}
        <ParallaxLayer offset={1} speed={2.5}>
          <TextBlock />
        </ParallaxLayer>
      </Parallax>
    );
}

export default Mainblock;

// ! The Parallax component creates a scrollable container in which ParallaxLayers can be placed . And we can add animation on those parallax layers.
